mapper.insert:
INSERT INTO t_user (username, password, salt, gender,
phone, email,avatar, is_delete,
created_user, created_time, modified_user, modified_time
) VALUES (
#{username}, #{password},#{salt}, #{gender},
#{phone}, #{email}, #{avatar}, #{isDelete},
#{createdUser}, #{createdTime}, #{modifiedUser}, #{modifiedTime}
)

mapper.select:
SELECT username, password, salt, is_delete isDelete
FROM
t_user WHERE id=#{id}

mapper.update:
UPDATE t_user
SET password=#{password}, modified_user=#{modifiedUser},
modified_time=#{modifiedTime}
WHERE id=#{id}
